mapper.select:
    	SELECT sample_no AS sampleNo
    		  ,sample_id AS sampleId
			  ,sample_pw AS samplePw
		FROM sample
		WHERE sample_id=#{sampleId}
		AND sample_pw=#{samplePw}

mapper.update:
UPDATE sample
SET sample_id =#{sampleId}
   ,sample_pw =#{samplePw}
WHERE sample_no =#{sampleNo}
